require('should');
const fs = require('fs');
const  app = require('../../index');
const server = app.listen();
const request = require('supertest').agent(server);

const ct = 'multipart/form-data; boundary=---------------------------paZqsnEHRufoShdX6fh0lUhXBP4k';
const body = [
    '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
    'Content-Disposition: form-data; name="file_name_0"',
    '',
    'super alpha file',
    '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
    'Content-Disposition: form-data; name="file_name_1"',
    '',
    'super beta file',
    '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
    'Content-Disposition: form-data; name="upload_file_0"; filename="1k_a.dat"',
    'Content-Type: application/octet-stream',
    '',
    '',
    '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
    'Content-Disposition: form-data; name="upload_file_1"; filename="1k_b.dat"',
    'Content-Type: application/octet-stream',
    '',
    '',
    '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k--'
].join('\r\n');

describe('Multipart Files', function() {
    after(function() {
        server.close();
    });

    it("should get token", function (done) {
        request
            .get('/token')
            .auth('tj', 'tobi')
            .expect(200)
            .end(function (err, res) {
                token = res.text;
                token.should.be.String,
                    cookie = res.headers["set-cookie"].join(';');
                if (err) done(err);
                done();
            });
    });


    it("should store all the files", function(done) {
        request.post('/upload')
        .auth('tj', 'tobi')
        .set('Conten-Type', ct)
        .set('Cookie', cookie)
        .set('x-csrf-token', token)
        .send(body)
        .expect(200)
        .end(function(err, res) {
            if(err) return done(err);
            const files = res.body;
            files.should.have.length(2);
            fs.stat(files[0], function(err) {
                if(err) return done(err);
                fs.stat(files[1], done);
            });
        });
    });
});
